sequence.script:
var req = new Namespace("http://wso2.org/request");
var payload = mc.getPayloadXML();
var manifest = payload.req::manifest.*;
var sourceDti = payload.req::dxi.dataTransferIndices.source.*;
mc.setPayloadXML(
  <req:dtoPageRequest xmlns:req="http://www.iringtools.org/dxfr/request">
    <req:manifest xmlns="http://www.iringtools.org/dxfr/manifest">{manifest}</req:manifest>
    <req:dataTransferIndices xmlns="http://www.iringtools.org/dxfr/dti"><dataTransferIndexList>{sourceDti}</dataTransferIndexList></req:dataTransferIndices>                  
  </req:dtoPageRequest>);

var req = new Namespace("http://wso2.org/request");
var payload = mc.getPayloadXML();
var manifest = payload.req::manifest.*;
var targetDti = payload.req::dxi.dataTransferIndices.target.*;
mc.setPayloadXML(
  <req:dtoPageRequest xmlns:req="http://www.iringtools.org/dxfr/request">
    <req:manifest xmlns="http://www.iringtools.org/dxfr/manifest">{manifest}</req:manifest>
    <req:dataTransferIndices xmlns="http://www.iringtools.org/dxfr/dti"><dataTransferIndexList>{targetDti}</dataTransferIndexList></req:dataTransferIndices>                  
  </req:dtoPageRequest>);

onComplete.script:
var soapenv = new Namespace("http://schemas.xmlsoap.org/soap/envelope/");
var payload = mc.getEnvelopeXML().soapenv::Body.*;                   
mc.setPayloadXML(
  <m:dtoAggrResponse xmlns:m="http://wso2.org/response">
   {payload}
  </m:dtoAggrResponse>);
